import axios from "axios";
import { useContext, useEffect, useState } from "react";
import Gc from "./Gc";

const Sum = () => {
  let obj = useContext(Gc);
  let [data,setData]=useState([])
  let [xt,setXt]=useState(0)
  let [total,setTotal]=useState(0)

    let getcart = () => {
      axios
        .get(`http://localhost:5000/getcart/${obj.usercon._id}`, {
          headers: { Authorization: obj.usercon.token },
        })
        .then((res) => {
          if (res.data.msg == undefined) {
            setData(res.data);
            let y = res.data;
            setXt(y.length)
            obj.usercon.cartlength = xt;
            let x = 0;
          for (let i = 0; i < y.length; i++) {
            x = x + y[i].qty * y[i].price;
          }
          setTotal(x);
          }
        });
    };
    useEffect(()=>{
      getcart()
    },[getcart])
  
  return (
    <div className=" bg-slate-500 z-50 fixed right-5 top-2/4 p-2 text-sm font-bold">
      <div>Item: <span>{xt}</span></div>
      <div>Price: ${total.toFixed(2)}</div>
    </div>
  );
};

export default Sum;
