import { useContext, useEffect, useState } from "react";
import Gc from "./Gc";

const Sum = () => {

    let getcart = () => {
        axios
          .get(`http://localhost:5000/getcart/${obj.usercon._id}`, {
            headers: { Authorization: obj.usercon.token },
          })
          .then((res) => {
            if (res.data.msg == undefined) {
              setData(res.data);
              let y = res.data;
              let x = 0;
              for (let i = 0; i < y.length; i++) {
                x = x + y[i].qty * y[i].price;
              }
              setTotal(x);
            }
          });
      };

  return (
    <div className="w-12 h-12 bg-slate-500 z-50 fixed right-5 top-2/4">
      <div>Item</div>
      <div>Price</div>
    </div>
  );
};

export default Sum;
