import React, { useContext } from "react";
import { Link } from "react-router-dom";
// import "./nav.css";
import Gc from "./Gc";
import logo from "../logo/logo.png";

const Nav = () => {
  let obj = useContext(Gc);

  let getcart = () => {
    axios
      .get(`http://localhost:5000/getcart/${obj.usercon._id}`, {
        headers: { Authorization: obj.usercon.token },
      })
      .then((res) => {
        if (res.data.msg == undefined) {
          setData(res.data);
          let y = res.data;
          let x = 0;
          for (let i = 0; i < y.length; i++) {
            x = x + y[i].qty * y[i].price;
          }
          setTotal(x);
        } else {
          setErr(res.data.msg);
        }
      });
  };


  return (
    <nav className="nav w-full h-7 fixed flex justify-between px-5 bg-gray-900 text-white">
      <Link to="/">
        <div className="logo w-10 h-5/6">
          <img className="w-full h-full" src={logo} />
        </div>
      </Link>
      <div className="flex gap-10">
        {!obj.usercon.islogin && (
          <Link to="/login">
            <div className="nav-login group">
              Login
            </div>
          </Link>
        )}

        <Link to="/cart">Cart</Link>
        {obj.usercon.islogin && obj.usercon.isadmin && (
          <Link to="/add">Addprod</Link>
        )}
        {obj.usercon.islogin && <Link to="/logout">Logout</Link>}
        {obj.usercon.islogin && <div>{obj.usercon.name}</div>}
      </div>
    </nav>
  );
};

export default Nav;
