import axios from "axios";
import { useContext, useEffect, useState } from "react";
import Gc from "./Gc";

const Sum = () => {
  let [data, setData] = useState([]);
  let obj = useContext(Gc);
  let [len, setLen] = useState("");
  let getcart = () => {
    axios
      .get(`http://localhost:5000/getcart/${obj.usercon._id}`, {
        headers: { Authorization: obj.usercon.token },
      })
      .then((res) => {
        if (res.data.msg == undefined) {
          setData(res.data);
          let y = res.data;
          setLen(y.length);
          obj.usercon.cartlength = len;
        }
      });
  };
  return (
    <div className="w-12 h-12 bg-slate-500 z-50 fixed right-5 top-2/4">
      <div>Item: {obj.usercon.cartlength}</div>
      <div>Price</div>
    </div>
  );
};

export default Sum;
