import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./nav.css";
import Gc from "./Gc";
import logo from "../logo/logo.png";

const Nav = () => {
  let obj = useContext(Gc);
  return (
    <nav className="nav">
      <Link to="/">
        <div className="logo">
          <img src={logo} />
        </div>
      </Link>
      {/* {!obj.usercon.islogin && <Link to="/reg">Register</Link>} */}

      <div className="nav-search">
        <input type="text" placeholder="Search for products"/>
      </div>

      {!obj.usercon.islogin && (
        <Link to="/login">
          <div className="nav-login">
            Login
            <div className="login-submenu">
              <div className="new-sign">
                <p>New Customer?</p>
                <p style={{ color: "blue" }}>
                  <Link to="/reg">Signup</Link>
                </p>
              </div>
            </div>
          </div>
        </Link>
      )}

      <Link to="/cart">Cart</Link>
      {obj.usercon.islogin && obj.usercon.isadmin && (
        <Link to="/add">Addprod</Link>
      )}
      {obj.usercon.islogin && <Link to="/logout">Logout</Link>}
      {obj.usercon.islogin && <div>{obj.usercon.name}</div>}
    </nav>
  );
};

export default Nav;
